mapper.select:
select

from t_custm_intention

    company_id=#{companyId}

        and telephone like CONCAT('%',#{telephone},'%')




              and (
                  (IF(IFNULL(intention_level,'E')='','E',IFNULL(intention_level,'E')) = #{intention}
                  and IF(IFNULL(focus_level,'E')='','E',IFNULL(focus_level,'E')) >= #{intention}
                  and IF(IFNULL(intent_level,'E') ='','E',IFNULL(intent_level,'E'))>=#{intention})
                  or
                  (IF(IFNULL(intention_level,'E')='','E',IFNULL(intention_level,'E')) >= #{intention}
                  and IF(IFNULL(focus_level,'E')='','E',IFNULL(focus_level,'E')) = #{intention}
                  and IF(IFNULL(intent_level,'E')='','E',IFNULL(intent_level,'E')) >=#{intention})
                  or
                  (IF(IFNULL(intention_level,'E')='','E',IFNULL(intention_level,'E')) >= #{intention}
                  and IF(IFNULL(focus_level,'E')>='','E',IFNULL(focus_level,'E')) >= #{intention}
                  and IF(IFNULL(intent_level,'E')='','E',IFNULL(intent_level,'E')) = #{intention})
              )


              and intention_level = #{intention}


              and focus_level = #{intention}


              and intent_level = #{intention}




              and (if(ifnull(intention_level,'E')='','E',ifnull(intention_level,'E')) < 'D'
                  or if(ifnull(focus_level,'E')='','E',ifnull(focus_level,'E')) < 'D'
                  or if(ifnull(intent_level,'E')='','E',ifnull(intent_level,'E')) < 'D')


              and (if(ifnull(intention_level,'E')='','E',ifnull(intention_level,'E')) < 'D')


              and (if(ifnull(focus_level,'E')='','E',ifnull(focus_level,'E')) < 'D')


              and (if(ifnull(intent_level,'E')='','E',ifnull(intent_level,'E')) < 'D')




        and begin_date >= #{beginDate}


        and end_date <= #{endDate}


        ORDER BY id DESC


        LIMIT #{begin},#{pageNum}

select
count(id)
from t_custm_intention

    company_id=#{companyId}

        and task_id = #{taskId}


        and telephone like CONCAT('%',#{telephone},'%')




                and (
                (IF(IFNULL(intention_level,'E')='','E',IFNULL(intention_level,'E')) = #{intention}
                and IF(IFNULL(focus_level,'E')='','E',IFNULL(focus_level,'E')) >= #{intention}
                and IF(IFNULL(intent_level,'E') ='','E',IFNULL(intent_level,'E'))>=#{intention})
                or
                (IF(IFNULL(intention_level,'E')='','E',IFNULL(intention_level,'E')) >= #{intention}
                and IF(IFNULL(focus_level,'E')='','E',IFNULL(focus_level,'E')) = #{intention}
                and IF(IFNULL(intent_level,'E')='','E',IFNULL(intent_level,'E')) >=#{intention})
                or
                (IF(IFNULL(intention_level,'E')='','E',IFNULL(intention_level,'E')) >= #{intention}
                and IF(IFNULL(focus_level,'E')>='','E',IFNULL(focus_level,'E')) >= #{intention}
                and IF(IFNULL(intent_level,'E')='','E',IFNULL(intent_level,'E')) = #{intention})
                )


                and intention_level = #{intention}


                and focus_level = #{intention}


                and intent_level = #{intention}




                and (if(ifnull(intention_level,'E')='','E',ifnull(intention_level,'E')) < 'D'
                or if(ifnull(focus_level,'E')='','E',ifnull(focus_level,'E')) < 'D'
                or if(ifnull(intent_level,'E')='','E',ifnull(intent_level,'E')) < 'D')


                and (if(ifnull(intention_level,'E')='','E',ifnull(intention_level,'E')) < 'D')


                and (if(ifnull(focus_level,'E')='','E',ifnull(focus_level,'E')) < 'D')


                and (if(ifnull(intent_level,'E')='','E',ifnull(intent_level,'E')) < 'D')




        and begin_date >= #{beginDate}


        and end_date <= #{endDate}
