mapper.select:
select

from t_manual

	company_id = #{companyId}

		and create_time >= #{beginDate}


		and create_time <= #{endDate}


		and status = #{status}


		and phone_num = #{phone}

	AND uid = #{uid} order by create_time desc

select 
from t_manual
where task_id = #{taskId} and company_id = #{companyId} and phone_num = #{phone}
