mapper.select:
select

from wx.t_set
where user_id = #{userId,jdbcType=INTEGER}
and value = #{phone}

select

from wx.t_set
where user_id = #{userId,jdbcType=INTEGER}
and value = #{groupId}

select

from wx.t_set
where id = #{id}
and user_id = #{userId}
and type = #{type}

select

from wx.t_set
where user_id = #{userId}
and value = #{value}

mapper.update:
UPDATE wx.t_set


        value = #{phone},


        voice_id = #{voiceId}




        id = #{id}


        AND user_id = #{userId}


        AND type = #{type};

UPDATE wx.t_set


        value = #{groupId},


        voice_id = #{voiceId}




        id = #{id}


        AND user_id = #{userId}


        AND type = #{type};
