mapper.select:
select

from call_task
where company_id = #{company_id,jdbcType=BIGINT}
AND task_name = #{taskName}
and status != 5
and status != 0

select

from call_task
where company_id = #{company_id,jdbcType=BIGINT}
AND task_name = #{taskName}
and status != 5
and status != 0

    and id != #{taskId}

select

FROM call_task
WHERE company_id = #{company_id}
AND id = #{id}

mapper.update:
update call_task
set task_state = '结束',
status = 3,
modify_date = #{modify_date}
where id = #{id}

mapper.select:
select count(*) FROM t_dialog
WHERE company_id = #{company_id}
AND task_id = #{taskid}
AND STATUS =1

select car_numbers from tm_customer
where task_id = #{task_id}
and ct_phone =#{ct_phone}
LIMIT 0,1

SELECT

from call_task
where uid=#{userId} and status=#{status}
