mapper.select:
SELECT DISTINCT t.menu_id menuId,t.url,m.id detailId,m.url detailUrl FROM ( SELECT tp.menu_id,tm.parent_id,tm.url FROM t_auh_permission tp LEFT JOIN t_auh_menu tm ON tp.menu_id=tm.id WHERE
tp.user_id=#{userId}
tp.position_id=#{positionId}
AND tm.is_validate=1 ) t LEFT JOIN t_auh_menu m ON t.menu_id=m.parent_id
