mapper.insert:
insert into tm_customer ( ctid, task_id,ctname, company_id, ct_address, ct_sex, ct_position, ct_phone,`car_numbers` , ct_type,cm_type) values
( #{item.ctid,jdbcType=VARCHAR},#{item.task_id,jdbcType=BIGINT}, #{item.ctname,jdbcType=VARCHAR},#{item.companyId,jdbcType=BIGINT}, #{item.ctAddress,jdbcType=VARCHAR}, #{item.ctSex,jdbcType=VARCHAR}, #{item.ctPosition,jdbcType=VARCHAR}, #{item.ctPhone,jdbcType=VARCHAR}, #{item.car_numbers,jdbcType=VARCHAR}, #{item.ctType,jdbcType=VARCHAR},#{item.cmType} )
ON DUPLICATE KEY UPDATE ctname=VALUES(ctname),car_numbers=VALUES(car_numbers);

mapper.select:
select

from tm_customer
where ct_phone = #{telephone}
AND company_id=#{companyId}
GROUP BY ct_phone,id, task_id,ctid, ctname, company_id, ct_address, ct_sex, ct_position, ct_type, car_numbers
